import styled from 'styled-components';

export const DisplayBox = styled.div`
  margin: 0 auto;
  background-color: hsl(0, 0%, 100%);
  max-width: 75%;
  min-width: 50%;
  display: flex;
  justify-content: space-evenly;
  border-radius: 5px;
`;
